$ErrorActionPreference = 'Stop'

Write-Host ""
Write-Host                                                                                   "      Colorized"
Write-Host                                                                                   "      ------------------ "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkGray'    -BackgroundColor 'Black' " Dim Screen Text    "
