Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Cyan'        -BackgroundColor 'Black' " Bright Screen Text "
Write-Host ""
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkGray'    -BackgroundColor 'White' " Popup Text         " 
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Gray'        -BackgroundColor 'White' " Dim Popup Text     " 
Write-Host ""
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkRed'     -BackgroundColor 'Black' " Red                "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Blue'        -BackgroundColor 'Black' " Orange             "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkYellow'  -BackgroundColor 'Black' " Yellow             "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkGreen'   -BackgroundColor 'Black' " Green              "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkCyan'    -BackgroundColor 'Black' " Cyan               "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkBlue'    -BackgroundColor 'Black' " Blue               "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Magenta'     -BackgroundColor 'Black' " Violet             "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'DarkMagenta' -BackgroundColor 'Black' " Magenta            "
Write-Host "" 
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Red'         -BackgroundColor 'Black' " Traffic Red        "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Yellow'      -BackgroundColor 'Black' " Traffic Amber      "
Write-Host -NoNewLine "     "; Write-Host -ForegroundColor 'Green'       -BackgroundColor 'Black' " Traffic Green      "
Write-Host ""
Write-Host                                                                                   "      Color Table"
Write-Host                                                                                   "      ------------------ "
Write-Host -NoNewLine " [00]"; Write-Host -ForegroundColor 'Black'       -BackgroundColor 'Black' " Black              "
Write-Host -NoNewLine " [01]"; Write-Host -ForegroundColor 'DarkBlue'    -BackgroundColor 'Black' " DarkBlue           "
