Write-Host -NoNewLine " [05]"; Write-Host -ForegroundColor 'DarkMagenta' -BackgroundColor 'Black' " DarkMagenta        "
Write-Host -NoNewLine " [06]"; Write-Host -ForegroundColor 'DarkYellow'  -BackgroundColor 'Black' " DarkYellow         "
Write-Host -NoNewLine " [07]"; Write-Host -ForegroundColor 'Gray'        -BackgroundColor 'Black' " Gray               "
Write-Host -NoNewLine " [08]"; Write-Host -ForegroundColor 'DarkGray'    -BackgroundColor 'Black' " DarkGray           "
Write-Host -NoNewLine " [09]"; Write-Host -ForegroundColor 'Blue'        -BackgroundColor 'Black' " Blue (Orange)      "
Write-Host -NoNewLine " [10]"; Write-Host -ForegroundColor 'Green'       -BackgroundColor 'Black' " Green              "
Write-Host -NoNewLine " [11]"; Write-Host -ForegroundColor 'Cyan'        -BackgroundColor 'Black' " Cyan               "
Write-Host -NoNewLine " [12]"; Write-Host -ForegroundColor 'Red'         -BackgroundColor 'Black' " Red                "
Write-Host -NoNewLine " [13]"; Write-Host -ForegroundColor 'Magenta'     -BackgroundColor 'Black' " Magenta (Violet)   "
